import "./GrafikBar.css";

const GrafikBar = (props) => {
let isiBar = '0%';

if(props.maxValue > 0){
    isiBar = Math.round((props.value/props.maxValue) * 100) + '%';
}
  return (
    <div className="chart-bar">
      <div className="chart-bar__inner">
          <div className="chart-bar__fill" style={{height: isiBar}}>
          </div>
      </div>
      <div className="chart-bar__label">
          {props.label}
      </div>
    </div>
  );
};

export default GrafikBar;
